import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Main from './components/MainPage';
import Login from './components/LoginPage';
import Signup from './components/SignupPage';
import ProfileCreate from './components/ProfileCreatePage';
import ProfileDetail from './components/ProfileDetailPage';
import ProfileList from './components/ProfileListPage';
import Header from './components/HeaderComponents';
import { initialProfiles } from './data/sampledata';
import './App.css';

function App() {
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [profiles, setProfiles] = useState(initialProfiles);

  const handleLogin = (username) => {
    setLoggedInUser(username);
    console.log('로그인 성공:', username);
  };

  const handleLogout = () => {
    setLoggedInUser(null);
    console.log('로그아웃 완료');
  };

  const handleProfileCreate = (newProfileData) => {
    const newId = profiles.length > 0 
      ? Math.max(...profiles.map(p => p.id)) + 1 
      : 1;
    
    const newProfile = {
      id: newId,
      ...newProfileData
    };
    
    setProfiles([...profiles, newProfile]);
    console.log('새 프로필 생성:', newProfile);
    alert('프로필이 성공적으로 생성되었습니다!');
  };

  // Layout 컴포넌트 (Header를 포함)
  const Layout = ({ children }) => (
    <>
      <Header loggedInUser={loggedInUser} onLogout={handleLogout} />
      {children}
    </>
  );

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Main />} />
          <Route path="/Login" element={<Login onLogin={handleLogin} />} />
          <Route path="/Signup" element={<Signup />} />
          <Route 
            path="/ProfileCreate" 
            element={
              <ProfileCreate 
                loggedInUser={loggedInUser} 
                onProfileCreate={handleProfileCreate} 
              />
            } 
          />
          <Route 
            path="/ProfileDetail/:id" 
            element={<ProfileDetail profiles={profiles} />} 
          />
          <Route 
            path="/ProfileList" 
            element={<ProfileList profiles={profiles} />} 
          />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
