import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './ProfileDetailPage.css';

export default function ProfileDetailPage({ profiles = [] }) {  // 기본값 추가
  const navigate = useNavigate();
  const { id } = useParams();

  // profiles가 없거나 빈 배열인 경우
  if (!profiles || profiles.length === 0) {
    return (
      <div className="detail-container">
        <div className="not-found">
          <h2 className="not-found-title">프로필 데이터를 불러올 수 없습니다.</h2>
          <button
            onClick={() => navigate('/ProfileList')}
            className="back-to-list-btn"
          >
            목록으로 돌아가기
          </button>
        </div>
      </div>
    );
  }

  const profile = profiles.find(p => p.id === parseInt(id));

  if (!profile) {
    return (
      <div className="detail-container">
        <div className="not-found">
          <h2 className="not-found-title">프로필을 찾을 수 없습니다.</h2>
          <button
            onClick={() => navigate('/ProfileList')}
            className="back-to-list-btn"
          >
            목록으로 돌아가기
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="detail-container">
      <button 
        onClick={() => navigate('/ProfileList')}
        className="back-link"
      >
        &larr; 목록으로 돌아가기
      </button>

      <div className="profile-detail">
        <h1 className="profile-detail-name">{profile.name}</h1>
        <p className="profile-detail-intro">{profile.intro}</p>
        <span className="profile-detail-job">
          {profile.job}
        </span>
        
        <hr className="divider" />

        <div className="strengths-section">
          <div>
            <h3 className="strengths-title">나의 장점</h3>
            <p className="strengths-content">{profile.strengths}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
